def text_to_num (s):
	# remove everything in parenthesis
	inside = False;
	new_s = "";
	multiplier = 1;
	nums = ["1","2","3","4","5","6","7","8","9","0","."];
	dec_found = False;

	if "billion" in s.lower():
		multiplier *= 1000000000;
	if "million" in s.lower():
		multiplier *= 1000000;
	if "thousand" in s.lower():
		multiplier *= 1000;
	for c in s:
		if c == "(" or c == "[":
			inside = True;
		if c == "k":
			multiplier = 1000;
		if (not inside) and (not c == "$") and (not c == "k") and (c in nums):
			if not (c == '.' and dec_found):
				new_s += c;
			if (c == '.'):
				dec_found = True;
		if c == ")" or c == "]":
			inside = False;
	if new_s == '.':
		new_s = '1';
	try:
		return str(int(float(new_s) * multiplier));
	except ValueError:
		return "";

